L[5072] = { title = "Nazan", description = "", abilityIcon = nil }
L[5085] = { title = "Pure Energie", description = "Vexallus beschwört pure Energie, wenn seine verbleibende Gesundheit um 15% verringert wird. [Heroisch] Vexallus beschwört zwei pure Energien gleichzeitig.", abilityIcon = nil }
L[5099] = { title = "Ellrys Dämmerweih", description = "", abilityIcon = nil }
L[5101] = { title = "Yazzai", description = "", abilityIcon = nil }
L[5104] = { title = "Apoko", description = "", abilityIcon = nil }
L[5167] = { title = "Flammenstoß", description = "Kael'thas Sonnenwanderer löst eine Flammensäule aus, die allen Spielercharakteren innerhalb von 6 Metern vom Einschlagspunkt 8 sekunden lang pro Sekunde Feuerschaden zufügt.", abilityIcon = 135826 }
L[5180] = { title = "Schockbarriere", description = "Kael'thas Sonnenwanderer umgibt sich mit einer Barriere, die 10000 Schaden absorbiert und ihn immun gegen Unterbrechungseffekte macht.", abilityIcon = 136051 }
L[5235] = { title = "Elementare beschwören", description = "Wenn Dunkelwirker Syth 90%, 55% und 15% seiner verbleibenden Gesundheit erreicht, beschwört er 4 Elementare zu seiner Verteidigung.", abilityIcon = 136029 }
L[5252] = { title = "Federsturm", description = "Anzu fängt einen Spielercharakter in einem Zyklon und verhindert so alle seine Aktionen. Der Spielercharakter ist dadurch jedoch 6 sekunden lang unverwundbar.", abilityIcon = 136018 }
L[5253] = { title = "Brut des Anzu", description = "Wenn Anzu 75% und 35% seiner verbleibenden Gesundheit erreicht, verbannt er sich selbst für 1 Minute, wodurch er gegen jeglichen Schaden immun und bewegungsunfähig wird. Während der Verbannung setzt Anzu weiterhin Fähigkeiten ein und beschwört Brut des Anzu zu seiner Verteidigung. Wenn die gesamte Brut des Anzu besiegt wird, bricht Anzu den Verbannungseffekt vorzeitig ab.", abilityIcon = 132926 }
L[5267] = { title = "Schatten anziehen", description = "Großmeister Vorpil teleportiert sich auf seine Plattform und beschwört alle Spielercharaktere zu sich. Nachdem er alle Spielercharaktere zu sich beschworen hat, ruft er einen Feuerregen herbei, der allen Spielercharakteren auf der Plattform 6 sekunden lang pro Sekunde Feuerschaden zufügt.", abilityIcon = 136221 }
L[5293] = { title = "Teufelsfeuer", description = "Zornseher Soccothrates schleudert alle Spielercharaktere im Umkreis von 10 Metern weg. Nachdem er alle Spielercharaktere weggestoßen hat, stürmt Zornseher Soccothrates auf einen Spielercharakter zu und hinterlässt eine Spur von Teufelsfeuer auf seinem Weg. Teufelsfeuer fügt darin befindlichen Spielercharakteren pro Sekunde Feuerschaden zu.", abilityIcon = 135798 }
L[5335] = { title = "Die Illusion des Herolds", description = "Wenn Herold Horizontiss 66% und 33% seiner verbleibenden Gesundheit erreicht, erzeugt er eine Illusion seiner selbst. Die Illusion des Herolds Horizontiss setzt Gedankenwunde ein, um Spielercharaktere anzugreifen.", abilityIcon = 135752 }
L[5388] = { title = "Brennende Nova", description = "Keli'dan der Zerstörer verbannt sich selbst. Er ist dadurch unverwundbar und fängt an, eine Feuernova zu wirken. Die Feuernova fügt allen Spielercharakteren im Umkreis von 20 Metern um Keli'dan Feuerschaden zu, wenn er den Zauber vollendet.", abilityIcon = 136135 }
L[5411] = { title = "Verstärkung beschwören", description = "Kommandantin Sarannis beschwört jede Minute 3 Reservisten und einen Heilkundigen der Blutwärter.", abilityIcon = 132333 }
L[5412] = { title = "Heilkundiger der Blutwärter", description = "", abilityIcon = nil }
L[5453] = { title = "Weißer Setzling", description = "", abilityIcon = nil }
L[5478] = { title = "Schösslinge beschwören", description = "Warpzweig zieht 6 Schösslinge auf, die ihm im Kampf helfen sollen. Nach 25 sekunden opfern sich die Schösslinge und heilen Warpzweig um die Menge ihrer verbleibenden Gesundheit.", abilityIcon = 136044 }
L[5488] = { title = "Inferno", description = "Tobende Flammen umgeben sich 8 sekunden lang mit einem Inferno, das allen Spielercharakteren in einem Umkreis von 10 Metern pro Sekunde Feuerschaden zufügt. Die tobenden Flammen können sich nicht bewegen oder Fähigkeiten einsetzen, solange sie in Flammen stehen. Wenn das Inferno endet, verfolgen sie ein neues Ziel.", abilityIcon = 135818 }
L[5894] = { title = "Zusammenschlagen", description = "Kriegshetzer O'mrogg wählt einen Spielercharakter zufällig aus und schlägt ihn nieder. O'mrogg verliert dabei sämtliche Bedrohung und gibt einen Großteil davon an das niedergeschlagene Ziel ab.", abilityIcon = 132345 }
L[5899] = { title = "Klingentanz", description = "Kriegshäuptling Kargath Messerfaust führt einen Klingentanz auf und stürzt sich auf mehrere zufällige Spielercharaktere. Dabei fügt er ihnen und allen Spielercharakteren in der Nähe körperlichen Schaden zu.", abilityIcon = 236303 }
L[5999] = { title = "Dampfhammers Mechaniker", description = "Wenn Robogenieur Dampfhammer 75%, 50% und 25% seiner verbleibenden Gesundheit erreicht, beschwört er Dampfhammers Mechaniker, die ihn reparieren.", abilityIcon = nil }
L[6003] = { title = "Zauberreflexion", description = "Kriegsherr Kalithresh umgibt sich mit Magie, die Zauber 8 sekunden lang in 100% der Fälle auf den Zaubernden zurückwirft.", abilityIcon = 132361 }
L[6006] = { title = "Tiefensumpfpilz", description = "Hungarfenn beschwört einen Tiefensumpfpilz an einer zufälligen Position, der nach 20 Sek. in einer Sporenwolke explodiert.", abilityIcon = 132371 }
L[6008] = { title = "Faulige Sporen", description = "Wenn Hungarfenn 20% seiner verbleibenden Gesundheit erreicht, setzt er faulige Sporen frei, die 11 sekunden lang pro Sekunde allen Spielercharakteren in einem Umkreis von 15 Metern 400 Gesundheit entziehen.", abilityIcon = 136100 }
L[6031] = { title = "Klaue", description = "Sumpffürst Musel'ek befiehlt Klaue, zufällig Spielercharaktere anzugreifen.", abilityIcon = nil }
L[6042] = { title = "Wächter der Ahn'kahar", description = "Urahne Nadox beschwört Wächter der Ahn'kahar, die ihn im Kampf verteidigen.", abilityIcon = 134969 }
L[6359] = { title = "Untertauchen", description = "Wenn Anub'arak 75%, 50% und 25% seiner verbleibenden Gesundheit erreicht, taucht er unter und ruft Neruber zu seiner Verteidigung herbei.", abilityIcon = 135773 }
L[6375] = { title = "Phase 1: Armee der Toten", description = "Novos der Beschwörer tritt in ein arkanes Feld ein und beschwört Angriffswellen von Untoten, um ihn zu verteidigen. Novos verbleibt in dem arkanen Feld, bis 4 Kristallwirker besiegt sind.", abilityIcon = nil }
