L[7491] = { title = "Ausbrennende Flammen", description = "Lavanthor fügt allen Spielercharakteren Feuerschaden zu und erhöht 3 Minuten lang ihren erlittenen Feuerschaden um 35%.", abilityIcon = 135830 }
L[7534] = { title = "Giftflasche", description = "Todespirscher Visceri wirft eine Giftflasche auf die Position eines zufälligen Spielercharakters. Das Gift bleibt 20 sekunden lang bestehen und fügt allen Zielen im Umkreis von 5 Metern Naturschaden zu.", abilityIcon = 134711 }
L[7577] = { title = "Erinnerung beschwören", description = "Sobald Argentumbeichtpatin Blondlocke 50% verbleibende Gesundheit erreicht, beschwört sie eine Kreatur aus der Vergangenheit der Spielercharaktere.", abilityIcon = 136200 }
