L[5167] = { title = "Golpe de llamas", description = "Kael'thas Caminante del Sol invoca un pilar de llamas que quema a todos los jugadores en un radio de 6 m del área objetivo y les inflige daño de Fuego cada segundo durante 8 segundos.", abilityIcon = 135826 }
L[5180] = { title = "Barrera de choque", description = "Kael'thas Caminante del Sol se rodea con una barrera que absorbe 10000 p. de daño y lo vuelve inmune a los efectos de interrupción.", abilityIcon = 136051 }
L[5235] = { title = "Invocar elementales", description = "Cuando la salud del tejeoscuro Syth llega al 90%, al 55% y al 15%, invoca 4 elementales para que lo defiendan.", abilityIcon = 136029 }
L[5252] = { title = "Ciclón de plumas", description = "Anzu atrapa a un jugador en un ciclón, lo que le impide realizar acción alguna, pero lo vuelve invulnerable durante 6 segundos.", abilityIcon = 136018 }
L[5253] = { title = "Linaje de Anzu", description = "Cuando la salud de Anzu alcanza el 75% y el 35%, este se destierra a sí mismo durante 1 min, lo que lo vuelve inmune a todo el daño y no le permite moverse. Mientras está desterrado, Anzu puede seguir utilizando facultades e invoca al linaje de Anzu para que lo defienda.Derrotar a la totalidad del linaje de Anzu provoca que Anzu cancele el efecto de destierro antes de tiempo.", abilityIcon = 132926 }
L[5267] = { title = "Extraer Sombras", description = "El maestro mayor Vorpil se teletransporta a su plataforma e invoca a todos los jugadores a su ubicación. Tras invocar a todos los jugadores, el maestro mayor Vorpil lanza una Lluvia de Fuego que provoca quemaduras a todos los jugadores sobre la plataforma y les inflige daño de Fuego cada segundo durante 6 segundos.", abilityIcon = 136221 }
L[5293] = { title = "Fuego vil", description = "El arúspice de cólera Soccothrates repele a todos los jugadores en un radio de 10 m. Tras repeler a todos los jugadores, el arúspice de cólera Soccothrates carga contra un jugador, lo que crea un muro de Fuego vil a su paso. Fuego vil inflige daño de Fuego cada segundo a todos los jugadores que estén en él.", abilityIcon = 135798 }
L[5335] = { title = "Ilusión del presagista", description = "Cuando la salud del presagista Cieloriss llega al 66% y al 33%, crea una ilusión de sí mismo. La ilusión del presagista Cieloriss usa Desgarro mental para atacar a los jugadores.", abilityIcon = 135752 }
L[5388] = { title = "Nova ardiente", description = "Keli'dan el Ultrajador se destierra a sí mismo, lo que lo vuelve invulnerable, y comienza a lanzar Nova de Fuego. Nova de Fuego inflige daño de Fuego a todos los jugadores en un radio de 20 m de Keli'dan cuando completa el lanzamiento.", abilityIcon = 136135 }
L[5411] = { title = "Invocar refuerzos", description = "La comandante Sarannis invoca a 3 reservistas Depositario de sangre y a 1 ensalmador Depositario de sangre cada minuto.", abilityIcon = 132333 }
L[5412] = { title = "Ensalmador Depositario de sangre", description = "", abilityIcon = nil }
L[5453] = { title = "Esqueje blanco", description = "", abilityIcon = nil }
L[5478] = { title = "Invocar pimpollos", description = "El disidente de distorsión alza 6 pimpollos para que le ayuden en la batalla. Tras 25 segundos, los pimpollos se sacrifican y sanan al disidente de distorsión una cantidad equivalente a su salud restante.", abilityIcon = 136044 }
L[5488] = { title = "Infierno", description = "Las llamas enfurecidas se rodean a sí mismas con llamas infernales durante 8 segundos, lo que inflige daño de Fuego a todos los jugadores en un radio de 10 m cada segundo. Mientras el infierno está activo, las llamas enfurecidas no pueden moverse ni utilizar facultades. Cuando termina, las llamas enfurecidas eligen un nuevo objetivo al que perseguir.", abilityIcon = 135818 }
L[5894] = { title = "Aplastar", description = "El belisario O'mrogg selecciona a un jugador aleatorio y lo aplasta, lo que despeja toda la amenaza y transfiere una gran cantidad de amenaza al objetivo aplastado.", abilityIcon = 132345 }
L[5899] = { title = "Danza de hojas", description = "El jefe de guerra Kargath Garrafilada inicia una Danza de hojas y carga contra varios jugadores aleatorios, lo que les inflige daño físico a ellos y a todos los jugadores cercanos.", abilityIcon = 236303 }
L[5999] = { title = "Mecánicos Vaporino", description = "Cuando la salud del mekigeniero Vaporino llega al 75%, 50% y 25%, invoca mecánicos Vaporino para que lo reparen.", abilityIcon = nil }
L[6003] = { title = "Reflejo de hechizos", description = "El señor de la guerra Kalithresh se protege con magia, lo que refleja un 100% de los hechizos al taumaturgo durante 8 segundos.", abilityIcon = 132361 }
L[6006] = { title = "Champiñón de la Sotiénaga", description = "Panthambre invoca champiñones de la Sotiénaga en una ubicación aleatoria. Estos estallan en una nube de esporas tras 20 s.", abilityIcon = 132371 }
L[6008] = { title = "Esporas hediondas", description = "Cuando a Panthambre le queda un 20% de salud, lanza Esporas hediondas que roban 400 p. de salud a todos los jugadores en un radio de 15 m cada segundo durante 11 segundos.", abilityIcon = 136100 }
