L[5167] = { title = "Frappe de flammes", description = "Kael’thas Haut-Soleil invoque un pilier de flammes brûlant tous les personnages-joueurs se trouvant à moins de 6 mètres de la zone ciblée et infligeant des dégâts de Feu toutes les secondes pendant 8 secondes.", abilityIcon = 135826 }
L[5180] = { title = "Barrière de choc", description = "Kael’thas Haut-Soleil s’entoure d’une barrière absorbant 10000 points de dégâts et prévenant tout effet d’interruption.", abilityIcon = 136051 }
L[5235] = { title = "Invocation d'élémentaires", description = "Quand le sombre tisseur Syth n’a plus que 90%, 55% ou 15% de ses points de vie, il invoque 4 élémentaires pour le défendre.", abilityIcon = 136029 }
L[5252] = { title = "Cyclone de plumes", description = "Anzu enveloppe un personnage-joueur d'un cyclone, l’empêchant d’agir mais le rendant invulnérable pendant 6 secondes.", abilityIcon = 136018 }
L[5253] = { title = "Progéniture d'Anzu", description = "Quand Anzu n’a plus que 75% ou 35% de ses points de vie, il se bannit lui-même pendant 1 minute, ce qui l’immunise contre tous les dégâts, mais l’empêche de se déplacer. Tant qu’il est banni, Anzu continue à utiliser ses techniques et invoque la progéniture d’Anzu pour le défendre.Si toute la progéniture d’Anzu est éliminée, Anzu annule son bannissement plus tôt.", abilityIcon = 132926 }
L[5267] = { title = "Attirer les ombres", description = "Le grand Maître Vorpil se téléporte sur sa plateforme et y attire à lui tous les personnages-joueurs. Il invoque ensuite une Pluie de feu infligeant à tous les personnages-joueurs de la plateforme des dégâts de Feu toutes les secondes pendant 6 secondes.", abilityIcon = 136221 }
L[5293] = { title = "Gangrefeu", description = "Le scrute-courroux Soccothrates repousse au loin tous les personnages-joueurs se trouvant à moins de 10 mètres. Il charge ensuite un personnage-joueur en laissant un mur de Gangrefeu sur son passage. Gangrefeu inflige des dégâts de Feu toutes les secondes à tous les personnages-joueurs pris dedans.", abilityIcon = 135798 }
L[5335] = { title = "Illusion du messager", description = "Quand il ne lui reste plus que 66% et 33% de ses points de vie, le messager Cieuriss invoque un double illusoire. L’illusion du messager Cieuriss attaque les personnages-joueurs à l’aide de Pourfendre l’esprit.", abilityIcon = 135752 }
L[5388] = { title = "Nova ardente", description = "Keli’dan le Briseur se bannit, devient invulnérable, et entame l’incantation d’une Nova de feu qui inflige des dégâts de Feu à tous les personnages-joueurs se trouvant à moins de 20 mètres de Keli’dan au moment où il termine son incantation.", abilityIcon = 136135 }
L[5411] = { title = "Invocation de renforts", description = "Le commandant Sarannis invoque 3 réservistes garde-sang ainsi qu’un réparateur garde-sang toutes les minutes.", abilityIcon = 132333 }
L[5412] = { title = "Réparateur garde-sang", description = "", abilityIcon = nil }
L[5453] = { title = "Bouture blanche", description = "", abilityIcon = nil }
L[5478] = { title = "Invocation d'arbrisseaux", description = "Brise-Dimension anime 6 arbrisseaux pour l’aider dans son combat. Au bout de 25 secondes, les arbrisseaux se sacrifient et soignent Brise-Dimension pour un montant égal à leurs points de vie restants.", abilityIcon = 136044 }
L[5488] = { title = "Inferno", description = "Les flammes violentes s’entourent d’un Inferno pendant 8 secondes et infligent des dégâts de Feu à tous les personnages-joueurs se trouvant à moins de 10 mètres toutes les secondes. Tant qu’elles sont englouties par les flammes, les flammes violentes ne peuvent pas se déplacer ni utiliser leurs techniques. Une fois l’Inferno éteint, les flammes violentes choisissent une nouvelle cible à poursuivre.", abilityIcon = 135818 }
L[5894] = { title = "Massue", description = "Le porteguerre O’mrogg choisit un personnage-joueur au hasard et le frappe, réduisant à zéro la menace de tous les ennemis puis donnant une grande quantité de menace à la cible frappée.", abilityIcon = 132345 }
L[5899] = { title = "Danse des lames", description = "Le chef de guerre Kargath Lamepoing se lance dans une Danse des lames. Il charge plusieurs personnages-joueurs au hasard et inflige des dégâts physiques à eux ainsi qu’à tous leurs alliés proches.", abilityIcon = 236303 }
L[5999] = { title = "Mécaniciens montevapeurs", description = "Quand il ne lui reste plus que 75%, 50% et 25% de ses points de vie, le mekgénieur montevapeur invoque des mécaniciens montevapeurs pour le réparer.", abilityIcon = nil }
L[6003] = { title = "Renvoi de sort", description = "Le seigneur de guerre Kalithresh s’entoure de magie, ce qui lui permet de renvoyer les sorts à leur lanceur dans 100% des cas pendant 8 secondes.", abilityIcon = 132361 }
L[6006] = { title = "Champignon de Basse-tourbière", description = "Hungarfen invoque à des emplacements aléatoires des champignons de Basse-tourbière qui explosent au bout de 20 s, libérant un nuage de spores.", abilityIcon = 132371 }
L[6008] = { title = "Spores nauséabondes", description = "Quand il ne lui reste plus que 20% de ses points de vie, Hungarfen lâche un nuage de Spores nauséabondes qui draine 400 points de vie aux personnages-joueurs se trouvant à moins de 15 mètres toutes les secondes pendant 11 secondes.", abilityIcon = 136100 }
L[6031] = { title = "Griffe", description = "Le seigneur des marais Musel’ek ordonne à Griffe d’attaquer des personnages-joueurs au hasard.", abilityIcon = nil }
L[6042] = { title = "Gardien ahn’kahar", description = "L’ancien Nadox invoque des gardiens ahn’kahar qui viennent le protéger.", abilityIcon = 134969 }
