L[5167] = { title = "Assalto delle Fiamme", description = "Kael'thas Solealto evoca una colonna di fuoco, bruciando tutti i personaggi entro 6 m dal centro dell'area bersaglio e infliggendo danni da fuoco aggiuntivi ogni secondo per 8 secondi.", abilityIcon = 135826 }
L[5180] = { title = "Barriera Folgorante", description = "Kael'thas Solealto si circonda con una barriera che assorbe 10000 danni, rendendolo immune agli effetti di interruzione.", abilityIcon = 136051 }
L[5235] = { title = "Evocazione: Elementali", description = "Quando raggiunge il 90%, il 55% e il 15% di salute restante, Syth il Mistico Oscuro evoca 4 elementali per assisterlo in battaglia.", abilityIcon = 136029 }
L[5252] = { title = "Ciclone di Piume", description = "Anzu imprigiona in un ciclone un personaggio, impedendogli qualsiasi azione, ma rendendolo invulnerabile per 6 secondi.", abilityIcon = 136018 }
L[5253] = { title = "Stirpe di Anzu", description = "Quando raggiunge il 75% e al 35% di salute restante, Anzu si esilia per 1 min, diventando immune a tutti i tipi di danni, ma restando immobilizzato. Mentre è esiliato, Anzu continua a utilizzare le sue abilità ed evoca la Stirpe di Anzu per difendersi.Sconfiggere tutti i membri della Stirpe di Anzu annulla l'effetto di esilio.", abilityIcon = 132926 }
L[5267] = { title = "Trazione delle Ombre", description = "Il Gran Maestro Vorpil si teletrasporta sulla sua piattaforma ed evoca tutti i personaggi vicino a sé. Una volta evocati, inizia a lanciare Pioggia di Fuoco, infliggendo danni da fuoco ogni secondo a tutti i personaggi sulla piattaforma per 6 secondi.", abilityIcon = 136221 }
L[5293] = { title = "Vilfuoco", description = "Soccothrates il Veggente dell'Ira spinge indietro tutti i personaggi entro 10 m. Dopo averli spinti, Soccothrates carica un personaggio, lasciandosi dietro una scia di Vilfuoco che infligge danni da fuoco ogni secondo a tutti i personaggi che vi entrano in contatto.", abilityIcon = 135798 }
L[5335] = { title = "Illusione dell'Araldo", description = "Quando raggiunge il 66% e il 33% di salute restante, l'Araldo Skyriss crea una illusione di se stesso. L'illusione attacca i personaggi utilizzando Squartamento Mentale.", abilityIcon = 135752 }
L[5388] = { title = "Esplosione Bruciante", description = "Keli'dan il Demolitore si esilia, diventando invulnerabile, e comincia a lanciare un'esplosione di fuoco. Esplosione Bruciante infligge danni da fuoco a tutti i personaggi entro 20 m da Keli'dan quando completa il lancio.", abilityIcon = 136135 }
L[5411] = { title = "Evocazione: Rinforzi", description = "Il Comandante Sarannis evoca 3 Riservisti Guardiasangue e un Guaritore Guardiasangue al minuto.", abilityIcon = 132333 }
L[5412] = { title = "Guaritore Guardiasangue", description = "", abilityIcon = nil }
L[5453] = { title = "Piantina Bianca", description = "", abilityIcon = nil }
L[5478] = { title = "Evocazione: Arboscello", description = "Scheggia Arcana evoca 6 Arboscelli ad aiutarlo in battaglia. Dopo 25 secondi, gli Arboscelli si sacrificano, curando Scheggia Arcana di un ammontare uguale alla loro salute restante.", abilityIcon = 136044 }
L[5488] = { title = "Inferno", description = "Le Fiamme Furenti vengono circondate da un inferno ardente per 8 secondi, infliggendo danni da fuoco a tutti i personaggi entro 10 m ogni secondo. In questo stato, le Fiamme Furenti non possono muoversi o usare abilità. Quando Inferno finisce, le Fiamme Furenti scelgono un nuovo bersaglio da inseguire.", abilityIcon = 135818 }
L[5894] = { title = "Mazzata", description = "L'Araldo della Guerra O'mrogg sceglie un bersaglio casuale e lo abbatte, azzerando tutta la minaccia e spostandone un'ingente quantità sul bersaglio di Mazzata.", abilityIcon = 132345 }
L[5899] = { title = "Lame Danzanti", description = "Il Capoguerra Kargath Manotagliente lancia Lame Danzanti contro diversi personaggi casuali, infliggendo danni fisici a loro e a tutti i personaggi vicini.", abilityIcon = 236303 }
L[5999] = { title = "Meccanici del Vapore", description = "Quando il Mekgeniere Vaporlesto raggiunge il 75%, il 50% e il 25% di salute restante, evoca dei Meccanici del Vapore che lo riparino.", abilityIcon = nil }
L[6003] = { title = "Rifletti Incantesimo", description = "Il Signore della Guerra Kalithresh si protegge con la magia, riflettendo gli incantesimi all'incantatore il 100% delle volte per 8 secondi.", abilityIcon = 132361 }
L[6006] = { title = "Fungo della Torbiera Sotterranea", description = "Fungilor evoca dei Funghi della Torbiera Sotterranea che esplodono dopo 20 s, generando una nube di spore.", abilityIcon = 132371 }
L[6008] = { title = "Spore Infette", description = "Quando Fungilor raggiunge il 20% di salute restante, libera delle spore infette, prosciugando 400 salute da tutti i personaggi entro 15 m ogni secondo per 11 secondi.", abilityIcon = 136100 }
L[6031] = { title = "Spiro", description = "Musel'ek il Signore delle Paludi ordina a Spiro di attaccare dei personaggi casuali.", abilityIcon = nil }
