L[5235] = { title = "정령 소환", description = "흑마술사 시스는 생명력이 90%, 55%, 15% 이하로 떨어지면 자신을 보호해줄 정령 4마리를 소환합니다.", abilityIcon = 136029 }
L[5252] = { title = "깃털 회오리", description = "안주가 회오리를 일으켜 플레이어를 감쌉니다. 플레이어는 6 초 동안 어떤 행동도 하지 못하지만 무적 상태가 됩니다.", abilityIcon = 136018 }
L[5253] = { title = "안주의 혈족", description = "안주는 생명력이 75%, 35% 이하로 떨어지면 1분 동안 다른 공간으로 사라져 모든 피해에 면역이 되고 이동할 수 없는 상태가 됩니다. 이 동안에도 안주는 계속 능력을 사용하며, 안주의 혈족을 소환하여 자신을 방어합니다. 안주의 혈족을 모두 처치하면 안주는 효과의 지속시간을 채우지 않고 다시 나타납니다.", abilityIcon = 132926 }
L[5267] = { title = "어둠 끌어내기", description = "단장 보르필이 단상으로 순간이동하고 모든 플레이어를 가까이 끌어당깁니다. 단장 보르필은 모든 플레이어를 소환한 후 불의 비를 불러내어 단상에 있는 모든 플레이어에게 6 초 동안 1초마다 화염 피해를 입힙니다.", abilityIcon = 136221 }
L[5293] = { title = "지옥불", description = "격노의 점술사 소코드라테스가 10미터 반경 내에 있는 모든 플레이어를 밀쳐내고, 모든 플레이어가 밀려난 후 한 명의 플레이어에게 돌진합니다. 격노의 점술사 소코드라테스가 돌진하면서 일으키는 지옥불은 그 안에 서 있는 모든 플레이어에게 1초마다 화염 피해를 입힙니다.", abilityIcon = 135798 }
L[5335] = { title = "선구자의 환영", description = "선구자 스키리스는 생명력이 각각 66%, 33%가 되면 자신의 환영을 만들어냅니다. 선구자 스키리스의 환영은 정신 분열을 사용하여 플레이어를 공격합니다.", abilityIcon = 135752 }
L[5388] = { title = "불타는 회오리", description = "파괴자 켈리단이 다른 차원에 들어서 무적 상태가 된 뒤, 불타는 회오리를 시전합니다. 켈리단이 주문을 모두 외우면, 그에게서 20미터 반경 내에 있는 모든 플레이어가 화염 피해를 입습니다.", abilityIcon = 136135 }
L[5411] = { title = "지원군 소환", description = "지휘관 새래니스는 1분마다 붉은경비대 보충병 3명과 붉은경비대 치유사 1명을 소환합니다.", abilityIcon = 132333 }
