L[5488] = { title = "불지옥", description = "사나운 불길이 8 초 동안 불지옥으로 자신을 둘러싸 10미터 반경 내에 있는 모든 플레이어에게 1초마다 화염 피해를 입힙니다. 불지옥이 활성화된 동안 사나운 불길은 움직이거나 능력을 사용할 수 없고, 불지옥이 꺼지면 새로운 대상을 선택하여 추격합니다.", abilityIcon = 135818 }
L[5894] = { title = "넘어뜨리기", description = "전쟁인도자 오므로그가 무작위 플레이어를 선택하여 넘어뜨립니다. 모든 위협 수준이 초기화되고, 넘어뜨리기의 대상에 대한 위협 수준이 크게 증가합니다.", abilityIcon = 132345 }
