L[5167] = { title = "Golpe Flamejante", description = "Kael'thas Andassol evoca um pilar de fogo, queimando todos os jogadores em um raio de 6 metros da área alvejada e causando dano de fogo a cada segundo por 8 segundos.", abilityIcon = 135826 }
L[5180] = { title = "Barreira de Choque", description = "Kael'thas Andassol se protege com uma barreira que absorve 10000 de dano e o torna imune a efeitos de interrupção.", abilityIcon = 136051 }
L[5235] = { title = "Evocar Elementais", description = "Quando o Tecetrevas Syth chega a 90%, 55% e 15% de vida, ele evoca 4 elementais para defendê-lo.", abilityIcon = 136029 }
L[5252] = { title = "Ciclone de Plumas", description = "Anzu envolve um jogador num ciclone, impedindo que realizem quaisquer ações, mas tornando-os invulneráveis por 6 segundos.", abilityIcon = 136018 }
L[5253] = { title = "Prole de Anzu", description = "Quando Anzu chega a 75% e 35% dos pontos de vida, ele bane a si mesmo por 1 minuto, ficando imune a todos os danos e incapaz de se mover. Enquanto estiver banido, Anzu continuará a usar habilidades e evocará a Prole de Anzu para defendê-lo. Derrotar toda a Prole de Anzu fará com que o efeito do banimento seja cancelado mais cedo.", abilityIcon = 132926 }
L[5267] = { title = "Atrair Sombras", description = "O Grão-mestre Vorpil se teleporta para sua plataforma e evoca os jogadores até ele. Após evocar todos os jogadores, o Grão-mestre Vorpil evoca uma Chuva de Fogo, queimando todos os jogadores na plataforma com dano de Fogo a cada 6 segundos.", abilityIcon = 136221 }
L[5293] = { title = "Fogovil", description = "O Iráugure Socótrates derruba todos os jogadores num raio de 10. Depois de derrubar todos os jogadores, o Iráugure Socótrates investe contra um jogador, deixando uma muralha de Fogovil em seu rastro. O Fogovil causa dano de Fogo a cada segundo a todos os jogadores em contato com ele.", abilityIcon = 135798 }
L[5335] = { title = "A Ilusão do Precursor", description = "Quando o Precursor Skiriss chega a 66% e 33% de vida, ele cria uma cópia ilusória de si mesmo. Essa cópia usa Dilaceração Mental para atacar os jogadores.", abilityIcon = 135752 }
L[5388] = { title = "Nova Ardente", description = "Keli'dan, o Quebrador, bane a si mesmo para se tornar invulnerável e começa a lançar Nova de Fogo. A Nova de Fogo causa dano de Fogo a todos os jogadores que estiverem num raio de 20 m de Keli'dan quando ele terminar o lançamento.", abilityIcon = 136135 }
L[5411] = { title = "Evocar Reforços", description = "O Comandante Sarannis evoca 3 Reservistas da Falange Sangrenta e um Reparador da Falange Sangrenta a cada minuto.", abilityIcon = 132333 }
L[5412] = { title = "Reparador da Falange Sangrenta", description = "", abilityIcon = nil }
L[5453] = { title = "Sementino Branco", description = "", abilityIcon = nil }
L[5478] = { title = "Evocar Brotos", description = "O Estilhaço Dimensional dá vida a 6 Brotos para ajudá-lo em batalha. Após 25 segundos, os Brotos se sacrificam, curando o Estilhaço Dimensional na mesma quantidade da sua vida restante.", abilityIcon = 136044 }
L[5488] = { title = "Inferno", description = "A Chamas Enraivecidas se protegem com um incêndio por 8 segundos, causando dano de Fogo a todos os jogadores em um raio de 10 m a cada segundo. Durante o incêndio as Chamas Enraivecidas não podem se mover ou usar habilidades. Quando o incêndio termina, as Chamas Enraivecidas escolhem um novo alvo para perseguir.", abilityIcon = 135818 }
L[5894] = { title = "Surra", description = "O Armipotente O'mrogg escolhe um jogador aleatório e dá uma surra nele, eliminando totalmente a ameaça e concedendo uma grande quantidade de ameaça ao alvo da Surra.", abilityIcon = 132345 }
L[5899] = { title = "Dança de Lâminas", description = "O Chefe Guerreiro Karrath Carpunhal começa uma Dança de Lâminas, investindo contra vários jogadores aleatórios e causando dano Físico a eles e a todos os jogadores próximos.", abilityIcon = 236303 }
L[5999] = { title = "Mecânicos de Badulak", description = "Quando o Mecangenheiro Badulak chega a 75%, 50% e 25% de vida, ele evoca Mecânicos de Badulak para consertá-lo.", abilityIcon = nil }
L[6003] = { title = "Reflexão de Feitiço", description = "O Senhor da Guerra Kalithresh se protege em magia, refletindo feitiços de volta ao lançador 100% das vezes por 8 segundos.", abilityIcon = 132361 }
L[6006] = { title = "Cogumelo do Brejo Oculto", description = "O Voracharco evoca Cogumelos do Brejo Oculto num local aleatório que explodem em uma nuvem de esporos após 20 s.", abilityIcon = 132371 }
L[6008] = { title = "Esporos Pútridos", description = "Quando Voracharco chega a 20% de vida ele libera esporos pútridos, sugando 400 pontos de vida de todos os jogadores em um raio de 15 metros a cada segundo por 11 segundos.", abilityIcon = 136100 }
L[6031] = { title = "Garra", description = "O Senhor do Pântano Musel'ek ordena que Garra ataque jogadores aleatórios.", abilityIcon = nil }
L[6042] = { title = "Guardião Ahn'kahar", description = "O Ancião Nadox evoca Guardiões Ahn'kahar para defendê-lo em combate.", abilityIcon = 134969 }
