L[7534] = { title = "Frasco de Veneno", description = "Sicário Viscerae arremessa um frasco de veneno em um jogador aleatório. O veneno permanece no chão por 20 segundos e causa dano de Natureza a quem estiver em um raio de 5 m.", abilityIcon = 134711 }
L[7577] = { title = "Evocar Memória", description = "Quando chega a 50% dos pontos de vida restantes, a Confessora Argêntea Albamecha evoca uma criatura de suas batalhas pregressas.", abilityIcon = 136200 }
