L[5167] = { title = "Поражение пламенем", description = "Кель'тас Солнечный Скиталец призывает столб пламени, обжигая игроков в радиусе 6 от области действия и нанося им урон от огня раз в секунду в течение 8 сек.", abilityIcon = 135826 }
L[5180] = { title = "Шоковая преграда", description = "Кель'тас Солнечный Скиталец окружает себя преградой, поглощающей 10000 ед. урона и защищающей его от прерывания заклинаний.", abilityIcon = 136051 }
L[5235] = { title = "Призыв элементалей", description = "Когда у темнопряда Сита остается 90%, 55% и 15% здоровья, он призывает 4-х элементалей для защиты.", abilityIcon = 136029 }
L[5252] = { title = "Вихрь перьев", description = "Анзу закручивает игрока в вихре, лишая возможности производить любые действия, но делая его неуязвимым на протяжении 6 сек.", abilityIcon = 136018 }
L[5253] = { title = "Потомок Анзу", description = "Когда здоровье Анзу падает до 75% и до 35%, он на 1 минуту отправляется в изгнание, что делает его неуязвимым к любому урону и неспособным передвигаться. Тем не менее, во время изгнания Анзу продолжает использовать способности и призывать потомков Анзу, чтобы те защищали его.Если все потомки Анзу будут побеждены, то Анзу вернется из изгнания раньше времени.", abilityIcon = 132926 }
L[5267] = { title = "Приманивание теней", description = "Великий мастер Ворпил телепортируется на свою платформу и призывает к себе всех игроков. После призыва всех игроков Великий мастер Ворпил вызывает Огненный ливень, который обжигает всех игроков на платформе, нанося им урон от огня каждую секунду в течение 6 сек.", abilityIcon = 136221 }
L[5293] = { title = "Огонь Скверны", description = "Провидец Гнева Соккорат отбрасывает всех игроков в радиусе 10 м. Затем Провидец Гнева Соккорат атакует игрока, создавая стену огня Скверны на своем пути. Огонь Скверны каждую секунду наносит урон от огня всем игрокам, находящимся в нем.", abilityIcon = 135798 }
L[5335] = { title = "Двойник Предвестника", description = "Когда здоровье Предвестника Скайрисса падает до уровня 66% или 33%, он создает своего двойника, который атакует игроков при помощи заклинания \"Разрыв разума\".", abilityIcon = 135752 }
L[5388] = { title = "Горящее кольцо", description = "Кели'дан Разрушитель отправляется в изгнание и становится неуязвимым, после чего начинает применять заклинание \"Кольцо огня\". Когда заклинание будет применено, кольцо огня нанесет урон от огня всем игрокам в радиусе 20 м от Кели'дана.", abilityIcon = 136135 }
L[5411] = { title = "Вызов подкрепления", description = "Командир Сараннис призывает 3-х кровостражей-резервистов и кровостража-лекаря каждую минуту.", abilityIcon = 132333 }
L[5412] = { title = "Кровостраж-лекарь", description = "", abilityIcon = nil }
