L[5478] = { title = "Вызов саженцев", description = "Узлодревень выращивает 6 саженцев, которые будут помогать ему в бою. По прошествии 25 сек. саженцы будут принесены в жертву, восстанавливая столько единиц здоровья Узлодревня, сколько у них осталось.", abilityIcon = 136044 }
L[5488] = { title = "Преисподняя", description = "Вокруг яростного пламени в течение 8 сек. держится адский жар, что каждую секунду наносит урон от огня всем игрокам в радиусе 10 м. Во время действия заклинания яростное пламя не может двигаться или использовать способности. Когда действие заклинания заканчивается, яростное пламя выбирает новую цель.", abilityIcon = 135818 }
L[5894] = { title = "Убой", description = "О'мрогг Завоеватель сбрасывает текущий уровень угрозы и избивает случайного игрока. Уровень угрозы этого игрока значительно повышается.", abilityIcon = 132345 }
L[5899] = { title = "Танец клинков", description = "Вождь Каргат Острорук начинает танец клинков, бросаясь к нескольким случайным игрокам и нанося физический урон им самим и всем игрокам рядом с ними.", abilityIcon = 236303 }
L[5999] = { title = "Механики паровой оснастки", description = "Когда у анжинера Паропуска остается 75%, 50% и 25% здоровья, он призывает механиков паровой оснастки, которые восстанавливают ему здоровье.", abilityIcon = nil }
L[6003] = { title = "Отражение заклинания", description = "Полководец Калитреш окружает себя магическим барьером, с вероятностью 100% отражающим заклинания в того, кто их применил. Время действия – 8 сек.", abilityIcon = 132361 }
L[6006] = { title = "Гриб Нижетопи", description = "Топеглад призывает грибы Нижетопи, которые появляются в случайном месте и через 20 сек. взрываются облаком спор.", abilityIcon = 132371 }
L[6008] = { title = "Гнилые споры", description = "Когда у Топеглада остается 20% здоровья, он выпускает гнилые споры, которые похищает 400 ед. здоровья у всех игроков в радиусе 15 м ежесекундно в течение 11 сек.", abilityIcon = 136100 }
