L[6042] = { title = "Ан'кахарский страж", description = "Старейшина Надокс призывает ан'кахарских стражей, чтобы те защищали его в бою.", abilityIcon = 134969 }
L[6359] = { title = "Погружение", description = "Когда уровень здоровья Ануб'арака достигает 75%, 50% и 25%, он погружается и призывает на помощь нерубов.", abilityIcon = 135773 }
